// Test the UI data flow
export function testUIDataFlow(conditions: any[]) {
  console.log('ğŸ§ª Testing UI data flow...');
  
  if (!conditions || conditions.length === 0) {
    console.error('âŒ No conditions provided to UI');
    return false;
  }
  
  const condition = conditions[0];
  
  // Test required fields for UI rendering
  const requiredFields = ['space', 'time_range', 'days', 'rules'];
  const missingFields = requiredFields.filter(field => !condition[field]);
  
  if (missingFields.length > 0) {
    console.error('âŒ Missing required fields:', missingFields);
    return false;
  }
  
  // Test space selection
  if (!Array.isArray(condition.space) || condition.space.length === 0) {
    console.error('âŒ Space selection is empty or invalid');
    return false;
  }
  
  // Test days selection
  if (!Array.isArray(condition.days) || condition.days.length === 0) {
    console.error('âŒ Days selection is empty or invalid');
    return false;
  }
  
  // Test time range format
  if (!condition.time_range.includes('â€“')) {
    console.error('âŒ Time range format is invalid');
    return false;
  }
  
  // Test rules structure
  if (!Array.isArray(condition.rules) || condition.rules.length === 0) {
    console.error('âŒ Rules array is empty or invalid');
    return false;
  }
  
  condition.rules.forEach((rule: any, index: number) => {
    if (!rule.condition_type || !rule.operator || !rule.value) {
      console.error(`âŒ Rule ${index + 1} is missing required fields`);
      return false;
    }
  });
  
  console.log('âœ… UI data flow test passed!');
  return true;
}

// Run all tests
export function runAllTests() {
  console.log('ğŸš€ Running all booking condition tests...');
  
  const conditions = testTimeBlockConditions();
  const uiTestPassed = testUIDataFlow(conditions);
  
  if (uiTestPassed) {
    console.log('ğŸ‰ All tests completed successfully!');
  } else {
    console.error('ğŸ’¥ Some tests failed - check the logs above');
  }
  
  return { conditions, uiTestPassed };
}
